    private void btnhapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnhapusActionPerformed
        // TODO add your handling code here:
          if(txtno.getText().equals("") ||txtjenis.getText().equals("") || txtmaxpinjaman.getText().equals("")|| txtbunga.getText().equals("")|| txtmaxangsuran.getText().equals("")){
            JOptionPane.showMessageDialog(null, "LENGKAPI DATA !", "Koperasi", JOptionPane.INFORMATION_MESSAGE);
        }else{
        try {
            String sql ="delete from jenispinjaman where no='"+txtno.getText()+"'";
            java.sql.Connection conn=(Connection)koneksi.getKoneksi();
            java.sql.PreparedStatement pst=conn.prepareStatement(sql);
            pst.execute();
            kode();
            JOptionPane.showMessageDialog(this, "berhasil di hapus");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, e.getMessage());
        }
        loadData();
       
        txtjenis.setText("");
      //  buttonGroup1.clearSelection();
        txtmaxpinjaman.setText("");
        txtbunga.setText("");
        txtmaxangsuran.setText("");
        }
    }//GEN-LAST:event_btnhapusActionPerformed

    private void btntambahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btntambahActionPerformed
        // TODO add your handling code here:
           if(txtno.getText().equals("") ||txtjenis.getText().equals("") || txtmaxpinjaman.getText().equals("")|| txtbunga.getText().equals("")|| txtmaxangsuran.getText().equals("")){
            JOptionPane.showMessageDialog(null, "LENGKAPI DATA !", "Koperasi", JOptionPane.INFORMATION_MESSAGE);
        }else{
            String idd = txtno.getText();
            String namaa = txtjenis.getText();
            String notelpp = txtmaxpinjaman.getText();
            String pekerjaann = txtmaxangsuran.getText();
            String alamatt = txtbunga.getText();
            String tgll = tgl.getText();
           
            
            try {
                Connection c = koneksi.getKoneksi();

                String sql = "INSERT INTO jenispinjaman VALUES (?, ?, ?, ?, ?, ?)";

                PreparedStatement p = c.prepareStatement(sql);
                p.setString(1, idd);
                p.setString(2, namaa);
                p.setString(3, notelpp);
                p.setString(4, pekerjaann);
                p.setString(5, alamatt);
                p.setString(6, tgll);
                p.executeUpdate();
                p.close();
                kode();
            } catch (SQLException e) {
                System.out.println("Terjadi Error");
            } finally {
                loadData();
              
                txtjenis.setText("");
            //    buttonGroup1.clearSelection();
                txtmaxpinjaman.setText("");
                txtbunga.setText("");
                txtmaxangsuran.setText("");

                JOptionPane.showMessageDialog(null, "Data berhasil tersimpan", "koperasi", JOptionPane.INFORMATION_MESSAGE);
            }
        }

    }//GEN-LAST:event_btntambahActionPerformed

    private void btnubahActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnubahActionPerformed
        // TODO add your handling code here:
           int i = tabelanggota.getSelectedRow();
        if (i == -1) {
            return;
        }
        String idd = (String) model.getValueAt(i, 0);
        try {
            Connection c = koneksi.getKoneksi();
            String sql = "UPDATE jenispinjaman SET jenis =  '" + txtjenis.getText() + "',maxpinjaman='" + txtmaxpinjaman.getText() + "',maxangsuran='" + txtmaxangsuran.getText() + "',bunga='" + txtbunga.getText() + "' WHERE  no ='" + idd + "'";
            PreparedStatement p = c.prepareStatement(sql);
            p.executeUpdate();
            p.close();
        } catch (SQLException e) {
            System.out.println("Terjadi Error");
        } finally {
            loadData();
            txtno.setText("");
            txtjenis.setText("");
         //   buttonGroup1.clearSelection();
            txtmaxpinjaman.setText("");
            txtmaxangsuran.setText("");
            txtbunga.setText("");
            btntambah.setEnabled(true);
            JOptionPane.showMessageDialog(null, "Data berhasil diubah", "koperasi", JOptionPane.INFORMATION_MESSAGE);
            txtno.requestFocus();
        }
        
    }//GEN-LAST:event_btnubahActionPerformed

    private void tabelanggotaMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelanggotaMouseClicked
        // TODO add your handling code here:
        btntambah.setEnabled(false);
        btnubah.setEnabled(true);
        
        int i = tabelanggota.getSelectedRow();
        if (i == -1) {
            return;
        }
        String idd = (String) model.getValueAt(i, 0);
        txtno.setText(idd);

        String jenis = (String) model.getValueAt(i, 1);
        txtjenis.setText(jenis);

        String pinjaman = (String) model.getValueAt(i, 2);
        txtmaxpinjaman.setText(pinjaman);
        
        String angsuran = (String) model.getValueAt(i, 3);
        txtmaxangsuran.setText(angsuran);

        String bungaa = (String) model.getValueAt(i, 4);
        txtbunga.setText(bungaa);
    }//GEN-LAST:event_tabelanggotaMouseClicked

