    private void txtcariKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtcariKeyReleased
        // TODO add your handling code here:
        model.getDataVector().removeAllElements();
        model.fireTableDataChanged();

        try {
            Connection c = koneksi.getKoneksi();
            Statement s = c.createStatement();

            String sql = "select * from jenispinjaman where no like '%" + txtcari.getText() + "%' or jenis like'%" + txtcari.getText() + "%' or maxpinjaman like'" + txtcari.getText() + "%' or maxangsuran like'" + txtcari.getText() + "%' or bunga like'%" + txtcari.getText() +"%'";
            ResultSet r = s.executeQuery(sql);

            while (r.next()) {
                Object[] o = new Object[5];
                o[0] = r.getString("no");
                o[1] = r.getString("jenis");
                o[2] = r.getString("maxpinjaman");
                o[3] = r.getString("maxangsuran");
                o[4] = r.getString("bunga");
                               
                model.addRow(o);
            }
            r.close();
            s.close();
        } catch (SQLException e) {
            System.out.println("Terjadi Error");
        }
    }//GEN-LAST:event_txtcariKeyReleased

